import { useState } from "react";

import { createAuthUserWithEmailAndPassword, createUserDocumentFromAuth } from "../../utils/firebase/firebase.utils";

const defaultFormFields = {
  displayName: '',
  email: '',
  password: '',
  confirmPassword: ''
};

const SignUpFrom = () => {
  const [ formFields, setFormFields ] = useState(defaultFormFields);
  const { displayName, email, password, confirmPassword } = formFields;

  const resetFormField = () => {
    setFormFields(defaultFormFields);
  }

  const handleSubmit = async (event) => {
    event.preventDefault();
    if(password !== confirmPassword) {
      return alert('Password and Confirmed Password do not match!');
    } 
    try {
      const { user } = await createAuthUserWithEmailAndPassword(email, password);
      createUserDocumentFromAuth(user, { displayName });
    } catch (err) {
      if(err.code === 'auth/email-already-in-use'){
        alert('Cannot create user! Email already in use');
      } else {
      console.log('You made this ERROR while trying to sign up with Email & Password:', err);
      }
    }
  }

  const handleChange = (event) => {
    const { name, value } = event.target; 
    setFormFields({...formFields, [name]: value}); // spread out object, modify one value
  }

  return (
    <div>
      <h1>Sign up with your email and password</h1>
      <form onSubmit={handleSubmit}>
        <label>Display Name</label>
        <input type="text" onChange={handleChange} name="displayName" value={displayName} required/>

        <label>Email</label>
        <input type="email" onChange={handleChange} name="email" value={email} required/>

        <label>Password</label>
        <input type="password" onChange={handleChange} name="password" value={password} autoComplete='hurensohn' required/>

        <label>Confirm Password</label>
        <input type="password" onChange={handleChange} name="confirmPassword" value={confirmPassword} autoComplete='hurensohn' required/>
        <button type="submit">Sign Up</button>
      </form>
    </div>
  );
};

export default SignUpFrom;
