import FormInput from "../form-input/form-input.component";
import Button from "../button/button.component";
import { getAuth, signInWithEmailAndPassword } from "firebase/auth";


const SignInForm = () => {
  const [ formFields, setFormFields ] = useState(defaultFormFields);
  const { email, password } = formFields;

  const handleSignIn = () => {
    try {
      const auth = getAuth();
      return signInWithEmailAndPassword(auth, email, password);
    } catch (err) {
      return alert('Following Error oquired while signing in:', err);
    }
  }

  return (
    <form onSubmit={handleSignIn}>
      <FormInput label="Email" type="email" onChange={handleChange} name="email" value={email} required/>
      <FormInput label="Password" type="password" onChange={handleChange} name="password" value={password} autoComplete='hurensohn' required/>
      <div className="buttons"> 
        <Button type="submit">Sign In</Button>
        <Button type="submit" buttonType="google">Sign In</Button>
      </div>
    </form>
  );
};

export default SignInForm;
