import { createContext, useState, useEffect } from "react";
import { onAuthStateChangeListener } from "../utils/firebase/firebase.utils";

// as the actual value you want to access 
export const UserContext = createContext({
  currentUser: null,
  setCurrentUser: () => null
});

export const UserProvider = ({ children }) => { // This Provider allows any of its child components to access the values inside of its useState
  const [ currentUser, setCurrentUser ] = useState(null);
  const value = { currentUser, setCurrentUser };

  useEffect(() => {
    const unsubscribe = onAuthStateChangeListener(() => {});
    return unsubscribe;
  }, []);

  return <UserContext.Provider value={value}>{ children }</UserContext.Provider>
};
